import React, {useLayoutEffect, useEffect, useCallback} from 'react';
import Sound from 'react-sound';
import boggartInstructions from '../audio/level3.m4a';
import rattling from "../audio/rattling.mp3";
import torture_reply from "../audio/boggart/";
import dancing_reply from "../audio/boggart/";
import funny_reply from "../audio/boggart/";
import tickle_reply from "../audio/boggart/";
import torture from "../audio/boggart/";
import tickle from "../audio/boggart/";
import dancing from "../audio/boggart/";
import funny from "../audio/boggart/";

function RenderReply(props) {
    return (
        <React.Fragment>
             <Sound
               url={props.reply}
               playStatus={Sound.status.PLAYING}
               autoLoad={true}
               loop={false}
               volume={100}
              />
        </React.Fragment>
    );
}

export function Boggart(props) {

    const handleExitClick = () => {
        props.exit();
    }

    const intro = <React.Fragment>
      <Sound
         url={rattling}
         playStatus={Sound.status.PLAYING}
         autoLoad={true}
         loop={false}
         volume={75}
      />
        <Sound
          url={boggartInstructions}
          playStatus={Sound.status.PLAYING}
          autoLoad={true}
          loop={true}
         volume={75}
         />
                  </React.Fragment>;

    const handleViewChange = props.handleViewChange === undefined ? null : props.handleViewChange;

    const[sound, setSound] = useState(intro);
    const[correct, setCorrect] = useState(false);

    const tickle = <React.Fragment>
                                  <Sound
                                     url={rain_spell}
                                     playStatus={Sound.status.PLAYING}
                                     autoLoad={true}
                                     loop={false}
                                     volume={75} />
                                  <Sound
                                     url={rain_sound}
                                     playStatus={Sound.status.PLAYING}
                                     autoLoad={true}
                                     loop={false}
                                     volume={75} />
                                   </React.Fragment>;

    const explosion_comp =   <React.Fragment>
                                 <Sound
                                    url={explosion_spell}
                                    playStatus={Sound.status.PLAYING}
                                    autoLoad={true}
                                    loop={false}
                                    volume={100} />
                                 <Sound
                                    url={explosion}
                                    playStatus={Sound.status.PLAYING}
                                    autoLoad={true}
                                    loop={false}
                                    volume={25} />
                                  </React.Fragment>;

    const sunlight = <React.Fragment>
                       <Sound
                          url={sunlight_spell}
                          playStatus={Sound.status.PLAYING}
                          autoLoad={true}
                          loop={false}
                          volume={100} />
                     </React.Fragment>;

    const scream = <React.Fragment>
                        <Sound
                           url={scream_spell}
                           playStatus={Sound.status.PLAYING}
                           autoLoad={true}
                           loop={false}
                           volume={100} />
                        <Sound
                           url={scream_sound}
                           playStatus={Sound.status.PLAYING}
                           autoLoad={true}
                           loop={false}
                           volume={25} />
                    </React.Fragment>;

    const handleTickle = () => {
        console.log(document.activeElement.id);
       setSound(<RenderReply reply={tickle_reply}/>);
    }

    const handleDancing= () => {
        setSound(<RenderReply reply={dancing_reply}/>);
    }

    const finishedLevel = () => {
        handleViewChange('level-two');
    }

    const handleFunny = () => {
       setSound(<Sound
                  url={funny_reply}
                  playStatus={Sound.status.PLAYING}
                  autoLoad={true}
                  loop={false}
                  volume={100}
                   onFinishedPlaying={finishedLevel}
                />);
    }

    const handleTorture = () => {
        setSound(<RenderReply reply={torture_reply}/>);
    }

    const handleSpace = () => {
       let id =  console.log(document.activeElement.id);
       switch(id) {
       case "tickle":
         handleTickle();
       break;
       case "funny":
       handleFunny();
       break;
       case "dancing":
       handleDancing();
       break;
       case "torture":
       handleTorture();
       break;
       }
    }

    const handleSelect = (name) => {
        console.log(name);
        switch(name) {
            case -1:
            setSound(tickle);
            break;
            case 1:
            setSound(funny);
            break;
            case 2:
            setSound(dancing);
            break;
            case 3:
            setSound(torture);
            break;
        }
    }

      const handleKey = useCallback((e) => {
           var event = window.event ? window.event : e;
              console.log(event);
           switch(e.key) {
           case "Tab":
           handleSelect(document.activeElement.tabIndex)
           break;
           case " ":
           handleSpace();
           break;
           case "Escape":
           props.exit();
           }
      }, [props]);

     useLayoutEffect(() => {
         document.addEventListener("keydown", handleKey);
               return function cleanup() {
               document.removeEventListener("keydown", handleKey);
             };
       });


    return (
      <div id="boggart-image">
        <div id="d-landing">
                  <h id="h-entrance" >
                        Welcome to level three!
                  </h>
                  <p id="p-landing">
                  Ah, you have encountered the boggart, a very mysterious creature! This creature knows all of your deepest, darkest fears and transforms into the thing that you are most afraid of. 
                  There is a special spell that you must use in order to defeat the boggart. Let’s see if you can figure out what spell to use… 
                  </p>
                  <div id="d-options">
                       <button id="tickle" tabIndex = "1" onClick={handleTickle}> Tickle Spell </button>
                       <button tabIndex="2" id="funny" onClick={handleFunny}>Funny Spell</button>
                       <button tabIndex="3" id="dancing" onClick={handleDancing}>Dancing Spell</button>
                       <button tabIndex="4" id="torture" onClick={handleTorture}>Torture Curse</button>
                       </div>
                       {sound}
                  <div id="d-button-holder">
                    <button id="b-landing" onClick={handleExitClick}>
                         Exit Maze
                    </button>
                  </div>
          </div>
      </div>
    );
}
