import React, {useLayoutEffect, useEffect, useCallback} from 'react';
import Sound from 'react-sound';
import boggartInstructions from '../audio/level3.m4a';
import rattling from "../audio/rattling.mp3";

function RenderReply(props) {
    return (
        <React.Fragment>
             <Sound
               url={props.reply}
               playStatus={Sound.status.PLAYING}
               autoLoad={true}
               loop={false}
               volume={100}
              />
        </React.Fragment>
    );
}

export function Boggart(props) {

    const handleExitClick = () => {
        props.exit();
    }

    const intro = <React.Fragment>
      <Sound
         url={rattling}
         playStatus={Sound.status.PLAYING}
         autoLoad={true}
         loop={false}
         volume={75}
      />
        <Sound
          url={boggartInstructions}
          playStatus={Sound.status.PLAYING}
          autoLoad={true}
          loop={true}
         volume={75}
         />
                  </React.Fragment>;

    const handleViewChange = props.handleViewChange === undefined ? null : props.handleViewChange;

    const[sound, setSound] = useState(intro);
    const[correct, setCorrect] = useState(false);

    const rain = <React.Fragment>
                                  <Sound
                                     url={rain_spell}
                                     playStatus={Sound.status.PLAYING}
                                     autoLoad={true}
                                     loop={false}
                                     volume={75} />
                                  <Sound
                                     url={rain_sound}
                                     playStatus={Sound.status.PLAYING}
                                     autoLoad={true}
                                     loop={false}
                                     volume={75} />
                                   </React.Fragment>;

    const explosion_comp =   <React.Fragment>
                                 <Sound
                                    url={explosion_spell}
                                    playStatus={Sound.status.PLAYING}
                                    autoLoad={true}
                                    loop={false}
                                    volume={100} />
                                 <Sound
                                    url={explosion}
                                    playStatus={Sound.status.PLAYING}
                                    autoLoad={true}
                                    loop={false}
                                    volume={25} />
                                  </React.Fragment>;

    const sunlight = <React.Fragment>
                       <Sound
                          url={sunlight_spell}
                          playStatus={Sound.status.PLAYING}
                          autoLoad={true}
                          loop={false}
                          volume={100} />
                     </React.Fragment>;

    const scream = <React.Fragment>
                        <Sound
                           url={scream_spell}
                           playStatus={Sound.status.PLAYING}
                           autoLoad={true}
                           loop={false}
                           volume={100} />
                        <Sound
                           url={scream_sound}
                           playStatus={Sound.status.PLAYING}
                           autoLoad={true}
                           loop={false}
                           volume={25} />
                    </React.Fragment>;

    const handleRain = () => {
        console.log(document.activeElement.id);
       setSound(<RenderReply reply={rain_reply}/>);
    }

    const handleExplosion= () => {
        setSound(<RenderReply reply={explosion_reply}/>);
    }

    const finishedLevel = () => {
        handleViewChange('level-two');
    }

    const handleSunlight = () => {
       setSound(<Sound
                  url={sunlight_reply}
                  playStatus={Sound.status.PLAYING}
                  autoLoad={true}
                  loop={false}
                  volume={100}
                   onFinishedPlaying={finishedLevel}
                />);
    }

    const handleScream = () => {
        setSound(<RenderReply reply={scream_reply}/>);
    }

    const handleSpace = () => {
       let id =  console.log(document.activeElement.id);
       switch(id) {
       case "rain":
         handleRain();
       break;
       case "sunlight":
       handleSunlight();
       break;
       case "explosion":
       handleExplosion();
       break;
       case "scream":
       handleScream();
       break;
       }
    }

    const handleSelect = (name) => {
        console.log(name);
        switch(name) {
            case -1:
            setSound(rain);
            break;
            case 1:
            setSound(explosion_comp);
            break;
            case 2:
            setSound(sunlight);
            break;
            case 3:
            setSound(scream);
            break;
        }
    }

      const handleKey = useCallback((e) => {
           var event = window.event ? window.event : e;
              console.log(event);
           switch(e.key) {
           case "ArrowRight":
             handleViewChange('level-two');
           break;
           case "ArrowLeft":
           handleViewChange('enter');
           break;
           case "Tab":
           handleSelect(document.activeElement.tabIndex)
           break;
           case " ":
           handleSpace();
           break;
           case "Escape":
           props.exit();
           }
      }, [props]);

     useLayoutEffect(() => {
         document.addEventListener("keydown", handleKey);
               return function cleanup() {
               document.removeEventListener("keydown", handleKey);
             };
       });


    return (
      <div id="boggart-image">
        <div id="d-landing">
                  <h id="h-entrance" >
                        You made it to level 3!
                  </h>
                  <p id="p-landing">
                  Ah, you have encountered the boggart, a very mysterious creature! This creature knows all of your deepest, darkest fears and transforms into the thing that you are most afraid of. 
                  There is a special spell that you must use in order to defeat the boggart. Let’s see if you can figure out what spell to use… 
                  </p>
                  <div id="d-button-holder">
                    <button id="b-landing" onClick={handleExitClick}>
                         Exit Maze
                    </button>
                  </div>
          </div>
      </div>
    );
}
