import React, { useState, useLayoutEffect, useCallback, useEffect } from 'react';
import Sound from 'react-sound';
import {AppView} from './appView';
import '../landing.css';
import magicSound from '../audio/zapsplat_fantasy_reversed_backwards_magical_glissando_001_46178.mp3';
import epicMusic from '../audio/bensound-epic.mp3';
import Speech from 'react-speech';

function Greeting(props) {

    const hpTheme = "https://ia801309.us.archive.org/28/items/HarryPotter-hedwigTheme/Harry_Potter_Theme_Song_Hedwigs_Theme.mp3";

    const handleViewChange = props.onClick === undefined ? null : props.onClick;

    const[sound, setSound] = useState( <Sound
                                           url={hpTheme}
                                           playStatus={Sound.status.PLAYING}
                                           autoLoad={true}
                                           loop={false}
                                        />);

    const style = {
        play: {
            button: {
            width: '28',
            height: '28',
            cursor: 'pointer',
            pointerEvents: 'none',
            outline: 'none',
            backgroundColor: 'yellow',
            border: 'solid 1px rgba(255,255,255,1)',
            borderRadius: 6
            },
        }
    };
    const[speech, setSpeech] = useState( <Speech 
                                            styles={style}
                                            text ="Welcome to our game! As a player, you are a student at Hogwarts who is competing in the Triwizard cup. 
                                            You are about to enter the maze, where you will use your keyboard to navigate through the obstacles of the maze. 
                                            Click the spacebar to begin!"
                                            pitch="1"
                                            rate="1"
                                            volume="1"
                                            lang="en-GB"
                                            voice="Google UK English Male"
                                            pause="true"
                                        />);

    const handleClick = () => {
        props.onClick();
    };

      const handleKey = useCallback((e) => {
             var event = window.event ? window.event : e;
                console.log(event);
             if(e.key === "ArrowRight") {
                     console.log("right arrow key pressed");
                      handleViewChange("enter");
                     }
        }, [props]);

       useLayoutEffect(() => {
           document.addEventListener("keydown", handleKey);
                 return function cleanup() {
                 document.removeEventListener("keydown", handleKey);
               };
         });

         useEffect(() => {
            document.getElementsByClassName("rs-play")[0].click();
            console.log(document.getElementsByClassName("rs-play")[0]);
         });

    const stopSound = () => {
       Sound.playStatus = Sound.status.STOPPED;
            setSound(
                <Sound
                     url={hpTheme}
                     playStatus={Sound.status.STOPPED}
                     autoLoad={true}
                     loop={false}
                 />
            );
            setSpeech(
                <Speech
                    //not sure what to put here
                />
            );
        }

    return (
       <div id="d-landing">
         <h id="h-landing" >
               Escape 580
         </h>
         <p id="p-landing">
           (Spoken) Welcome to our game! As a player, you are a student at Hogwarts who is competing in the Triwizard cup. You are about to enter the maze, where you will use your keyboard to navigate through the obstacles of the maze. Click the spacebar to begin!
         </p>
         <div id="b-holder">
            <button id="b-landing" onClick={handleClick}>
                     Begin
                     </button>
                     <button id="b-landing" onClick={stopSound}>
                     STOP SOUND
          </button>
         </div>
        {sound}
        {speech}
       </div>
    );
}

export function Landing(props) {

     const[view, setView] = useState('greetings');

     const handleClick = () => {
           setView('app-view');
         };

    const[viewDisplay, setDisplay]= useState(<Greeting onClick={handleClick}/>);

    useLayoutEffect(() => {
        switch(view) {
         case 'greetings':
         setDisplay(<Greeting onClick={handleClick}/>);
         break;
         case 'app-view':
         setDisplay(<AppView/>);
         break;
         default:
         setDisplay(<Greeting onClick={handleClick}/>);
        }
    }, [view]);

 return (
    <div id="d-container">
        {viewDisplay}
    </div>
 );
}
